insert into LOLHEROS(ID,NICKNAME,NAME_CN,NAME_EN,TYPE,STORY) values(2,'光辉女郎','拉克丝','Lux','Mage','她是一个超远程的法师，一般走位不失误，对方很难能抓住她，所以拉克丝可以纯粹地堆自己的法伤。') 
insert into LOLHEROS(ID,NICKNAME,NAME_CN,NAME_EN,TYPE,STORY) values(3,'卡牌大师','崔斯特','Twisted Fate','Mage','他是一个拥有不少独特技能的AP英雄。抽牌和大招使崔斯特成为一个让人感到恐惧的英雄。魔法回复和造成更多伤害，AOE减速和中等伤害，或者眩晕加上少许伤害之间的选择，甚至能扭转战局，同样也使崔斯特成为一个多面手。') 
insert into LOLHEROS(ID,NICKNAME,NAME_CN,NAME_EN,TYPE,STORY) values(4,'盲僧','李青','Lee Sin','Assassin','李青是一个近战战士型英雄，拥有很高的机动性和爆发力，单挑和小规模团战能力很强，同时李青也是非常优秀的打野英雄，非常擅长野区的遭遇战和Gank，是非常致命的英雄人物。') 
insert into LOLHEROS(ID,NICKNAME,NAME_CN,NAME_EN,TYPE,STORY) values(5,'影流之主','劫','Jie','Assassin','劫是一名以消耗能量为主的近战刺客，他拥有超高的技能爆发，在线上也具备十足的威胁力。他的定位和之前的刺客英雄泰隆很像，但相比泰隆劫有着更为强大的后期。') 
insert into LOLHEROS(ID,NICKNAME,NAME_CN,NAME_EN,TYPE,STORY) values(6,'德邦总管','赵信','Xin Zhao','Fighter','属于德玛西亚阵营，赵信是光盾王朝的私人管家。而在游戏中的赵信则是一个爆发能力极高的战士英雄，他也是一名优秀的团战发起者，能够以迅雷不及掩耳的速度突破对方的防线，对对方的核心英雄造成致命威胁，但是赵信也有着装备没成型之前过于脆弱，没有逃生手段等等劣势') 
